const Koa = require('koa')
const logger = require('koa-logger')

const parser = require('../')

const port = 3000
const app = new Koa()

app.use(logger())

app.use(parser())

app.use(async (ctx, next) => {
  console.log(ctx.request.body)
  ctx.body = ctx.request.body
  await next()
})

app.listen(port)
console.log(`listening on port ${port}`)
